mapper.select:
select maxaccept, PATIENT_TYPE, PATIENT_SEX, PATIENT_NAME,PATIENT_TYPE_NAME, PATIENT_SEX_NAME, PATENT_NATION,CARD_ID,TEL,PHONE,ADDRESS,STREET_COMM,PATHOGENY,NOW_PRESENT,HIS_PRESENT, C_CARD_ID,C_CARD_SUB,GU_NAME,GU_TEL,GU_RELATIVE,EM_NAME,EM_TEL,EM_RELATIVE,CARD_POSITIVE, CARD_OTHER,C_CARD_INDEX,C_CARD_PHOTO,HB_INDEX,HB_SELF,TOGETHER_PHONE,CREATE_TIME, UPDATE_TIME,UPDATE_OPR,UPDATE_OPR_NAME,MAKE_FLAG,MAKE_FLAG_NAME,ABLE_FLAG, FW_TIMES,SERVER_TIME,SERVER_OPR,SERVER_OPR_NAME,VERSION from ( select a.maxaccept, a.patient_type, a.patient_sex, a.patient_name,a.patent_nation,a.card_id, a.tel,a.phone,a.address,a.street_comm,a.pathogeny,a.now_present,a.his_present,a.c_card_id, a.c_card_sub,a.gu_name,a.gu_tel,a.gu_relative,a.em_name,a.em_tel,a.em_relative,a.card_positive, a.card_other,a.c_card_index,a.c_card_photo,a.hb_index,a.hb_self,a.together_phone, to_char(a.create_time, 'yyyy-mm-dd HH24:mi:ss') CREATE_TIME, to_char(a.update_time, 'yyyy-mm-dd HH24:mi:ss') UPDATE_TIME, a.update_opr,a.update_opr_name,a.make_flag,a.able_flag,a.fw_times, a.server_time,a.server_opr,a.server_opr_name,a.version, (select code_name from t_common_code t where t.code_key='PATIENT_TYPE' and t.code_id = a.patient_type) PATIENT_TYPE_NAME, (select code_name from t_common_code t where t.code_key='MAKE_FLAG' and t.code_id=a.make_flag) MAKE_FLAG_NAME, (select code_name from t_common_code t where t.code_key='PATIENT_SEX' and t.code_id = a.patient_sex) PATIENT_SEX_NAME from t_patient a where a.able_flag=10900
and CARD_ID=#{cardID}
and PATIENT_NAME=#{name}
and PATIENT_TYPE=#{type}
and CREATE_TIME>=#{beginTime}
and CREATE_TIME<=#{endTime}
)b order by create_time desc

select MAXACCEPT,PATIENT_TYPE,PATIENT_NAME,PATIENT_SEX,PATENT_NATION, CARD_ID,TEL,PHONE,ADDRESS,STREET_COMM,PATHOGENY,NOW_PRESENT,HIS_PRESENT, C_CARD_ID,C_CARD_SUB,GU_NAME,GU_TEL,GU_RELATIVE,EM_NAME,EM_TEL,EM_RELATIVE, CARD_POSITIVE,CARD_OTHER,C_CARD_INDEX,C_CARD_PHOTO,HB_INDEX,HB_SELF,TOGETHER_PHONE, to_char(CREATE_TIME,'yyyy-mm-dd HH24:mi:ss') CREATE_TIME, to_char(UPDATE_TIME,'yyyy-mm-dd HH24:mi:ss') UPDATE_TIME, UPDATE_OPR,UPDATE_OPR_NAME,MAKE_FLAG,ABLE_FLAG, FW_TIMES, to_char(SERVER_TIME,'yyyy-mm-dd HH24:mi:ss') SERVER_TIME, SERVER_OPR,SERVER_OPR_NAME,VERSION from t_patient where 1=1
and CARD_ID=#{patientCardNO}
and MAXACCEPT=#{maxaccept}
and ABLE_FLAG=#{ableFlag}
and (PATIENT_NAME=#{patientName} or PHONE=#{patientPhone})
order by create_time desc

select MAXACCEPT,PATIENT_TYPE,PATIENT_NAME,PATIENT_SEX,PATENT_NATION, CARD_ID,TEL,PHONE,ADDRESS,STREET_COMM,PATHOGENY,NOW_PRESENT,HIS_PRESENT, C_CARD_ID,C_CARD_SUB,GU_NAME,GU_TEL,GU_RELATIVE,EM_NAME,EM_TEL,EM_RELATIVE, CARD_POSITIVE,CARD_OTHER,C_CARD_INDEX,C_CARD_PHOTO,HB_INDEX,HB_SELF,TOGETHER_PHONE, to_char(CREATE_TIME,'yyyy-mm-dd HH24:mi:ss') CREATE_TIME, to_char(UPDATE_TIME,'yyyy-mm-dd HH24:mi:ss') UPDATE_TIME, UPDATE_OPR,UPDATE_OPR_NAME,MAKE_FLAG,ABLE_FLAG, FW_TIMES, to_char(SERVER_TIME,'yyyy-mm-dd HH24:mi:ss') SERVER_TIME, SERVER_OPR,SERVER_OPR_NAME,VERSION from t_patient_temp where 1=1
and CARD_ID=#{patientCardNO}
and MAXACCEPT=#{maxaccept}
and ABLE_FLAG=#{ableFlag}
and UPDATE_OPR=#{oprID}

mapper.update:
update t_patient
version=#{versionAdd}, make_flag=#{makeFlag},
UPDATE_OPR=#{updateOpr}, UPDATE_OPR_NAME=#{updateOprName},UPDATE_TIME=sysdate, SERVER_TIME=to_date(#{serverTime}, 'yyyy-mm-dd HH24:mi:ss'), FW_TIMES=#{fwTimes}, SERVER_OPR=#{serverOpr}, SERVER_OPR_NAME=#{serverOprName},
fw_times=decode(fw_times, '0', '0', fw_times-1), fw_times=fw_times+1,
WHERE maxaccept=#{maxaccept}
and version=#{version}

update t_patient_temp
PATIENT_TYPE=#{patientType}, PATIENT_NAME=#{patientName}, PATIENT_SEX=#{patientSex}, PATENT_NATION=#{patientNation}, CARD_ID=#{cardID}, TEL=#{patientPhone}, PHONE=#{patientPhone}, ADDRESS=#{patientAddress}, STREET_COMM=#{streetComm}, PATHOGENY=#{patientPathogeny}, NOW_PRESENT=#{nowPresent}, HIS_PRESENT=#{hisPresent}, C_CARD_ID=#{cCardID}, C_CARD_SUB=#{cCardSub}, GU_NAME=#{guName}, GU_TEL=#{guPhone}, GU_RELATIVE=#{guRelative}, EM_NAME=#{guName}, EM_TEL=#{guPhone}, EM_RELATIVE=#{guRelative}, CARD_POSITIVE=#{cardPositive}, CARD_OTHER=#{cardOther}, C_CARD_INDEX=#{cCardIndex}, C_CARD_PHOTO=#{cCardPhoto}, HB_INDEX=#{hbIndex}, HB_SELF=#{hbSelf}, TOGETHER_PHONE=#{togetherPhoto}, UPDATE_OPR=#{oprID}, UPDATE_OPR_NAME=#{oprName}, MAKE_FLAG='10701', MAKE_FLAG='10702',
ABLE_FLAG=#{ableFlag}, FW_TIMES=FW_TIMES+1, SERVER_TIME=sysdate, SERVER_OPR=#{oprID}, SERVER_OPR_NAME=#{oprName}, UPDATE_TIME=sysdate
where ABLE_FLAG='10900'
and maxaccept=#{patientID}
and UPDATE_OPR=#{oprID}

mapper.select:
select 
  maxaccept, patient_id, cj_type, cj_level, create_time, update_opr, update_opr_name, able_flag 
from t_cj_mapping where 1=1 

	and patient_id=#{maxaccept}


	and able_flag=#{ableFlag}


	and cj_type=#{cjType}


	and cj_level=#{cjLevel}
