mapper.insert:
insert into T_FREE_BUSY_STATUS (
	MAXACCEPT,SEAT_ID,STATUS,CREATE_TIME
) values (
	#{maxaccept},#{seatID},#{status}, sysdate
)

mapper.update:
update T_CUST
CUST_NAME=#{custName}, SEX=#{custSex}, CUST_AGE=#{custAge}, CONN_PHONE=#{connPhone}, CUST_ADDR=#{custAddr}, CUST_TYPE=#{custType}, MARK=#{custMark}, UPDATE_OPR_ID=#{oprID}, UPDATE_TIME=sysdate
where MAXACCEPT=#{maxaccept}
