mapper.select:
select maxaccept,user_account,user_pwd,user_level,dept_code,dept_name,user_name,phone,tel,address,user_role,role_name from (
	select n.maxaccept,n.user_account,n.user_pwd,n.user_level, n.dept_code, m.dept_name, n.user_name, n.phone, n.tel, n.address, n.user_role, n.role_name from (
	       select a.maxaccept, a.user_account, a.user_pwd, a.user_level, a.dept_code, a.user_name, a.phone, a.tel, a.address, a.user_role, b.role_name from t_user a
	       left join t_role b on a.user_role = b.maxaccept where 1=1 

				and b.maxaccept=#{roleCode}

	       order by MAXACCEPT asc
	) n left join t_dept m on n.dept_code = m.maxaccept
) where 1=1 

	and user_name=#{userName}



		and dept_code in (
			select maxaccept from t_dept
			start with maxaccept=#{deptCode}
			connect by prior maxaccept = p_id
		)




		and maxaccept=#{maxaccept}

select a.MAXACCEPT, a.BLACK_PHONE, a.BLACK_REASON, a.UPDATE_OPR_ID, b.USER_NAME CREATE_OPR_NAME, to_char(a.UPDATE_TIME, 'yyyy-mm-dd HH24:mi:ss') UPDATE_TIME, a.ABLE_FLAG from T_BLACK_LIST a, T_USER b where a.UPDATE_OPR_ID=b.MAXACCEPT
and a.BLACK_PHONE=#{blackPhone}
order by a.UPDATE_TIME desc

mapper.update:
update T_BLACK_LIST
BLACK_PHONE=#{blackPhone}, BLACK_REASON=#{blackReason}, UPDATE_OPR_ID=#{oprID}, UPDATE_TIME=sysdate
where MAXACCEPT=#{maxaccept}

update T_SMS_MOULD
MOULD_TITLE=#{mouldTitle}, MOULD_CONTENT=#{mouldContent}, CREATE_OPR_ID=#{oprID}, CREATE_OPR_NAME=#{oprName}, DEPT_CODE=#{deptCode}, CREATE_TIME=sysdate
where MAXACCEPT=#{maxaccept}

mapper.select:
select MAXACCEPT,CUST_ID,CUST_NAME,PHONE,to_char(YY_SEND_TIME, 'yyyy-mm-dd HH24:mi:ss') YY_SEND_TIME,to_char(SJ_SEND_TIME, 'yyyy-mm-dd HH24:mi:ss') SJ_SEND_TIME, CREATE_OPR_ID,CREACREATE_OPR_NAME,SMS_CONTENT,DEPT_CODE,to_char(UPDATE_TIME, 'yyyy-mm-dd HH24:mi:ss') UPDATE_TIME, to_char(CREATE_TIME, 'yyyy-mm-dd HH24:mi:ss') CREATE_TIME, SEND_FLAG, SEND_MARK from T_SMS_LIST_HIS where 1=1
and DEPT_CODE in (select maxaccept from t_dept start with maxaccept=#{deptCode} connect by prior maxaccept = p_id)
and PHONE=#{smsPhone}
and SEND_FLAG=#{sendFlag}
order by CREATE_TIME desc
